  --popover-border: 210 6% 20%;
  --primary: 195 85% 42%;
  --primary-foreground: 195 10% 98%;
  --secondary: 210 8% 20%;
  --secondary-foreground: 210 5% 90%;
  --muted: 210 8% 18%;
  --muted-foreground: 210 5% 65%;
  --accent: 210 10% 20%;
  --accent-foreground: 210 5% 88%;
  --destructive: 0 72% 38%;
  --destructive-foreground: 0 10% 98%;
  --input: 210 12% 32%;
  --ring: 195 85% 42%;
  --chart-1: 195 75% 65%;
  --chart-2: 210 65% 68%;
  --chart-3: 180 60% 70%;
  --chart-4: 220 55% 72%;
  --chart-5: 160 58% 68%;
  --shadow-2xs: 0px 2px 0px 0px hsl(210 12% 5% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(210 12% 5% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(210 12% 5% / 0.00), 0px 1px 2px -1px hsl(210 12% 5% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(210 12% 5% / 0.00), 0px 1px 2px -1px hsl(210 12% 5% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(210 12% 5% / 0.00), 0px 2px 4px -1px hsl(210 12% 5% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(210 12% 5% / 0.00), 0px 4px 6px -1px hsl(210 12% 5% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(210 12% 5% / 0.00), 0px 8px 10px -1px hsl(210 12% 5% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 12% 5% / 0.00);

/* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
